<div id="catalog_container">    <? if (ADMIN_ID):?>      <div style="position:absolute; top:400px; right:30px;">        <a href="/catalog/getWithoutPhoto">Показать товары без фотографий</a>      </div>    <? endif;?>    <div class="category">      <ul>        <? foreach ($child_categories as $item):?>        <li><a href="/catalog/category/<?=$current_category;?>_<?=$item->id;?>"><?=$item->name;?></a></li>        <? endforeach;?>      </ul>    </div>    <hr class="clear" />        <? if ( !empty($tovars['search']) ):?>    <ul class="products">      <? foreach ($tovars['search'] as $key => $tovar):?>      <li class="product">        <div class="top"></div>        <div class="bg">          <div class="name" title="<?=$tovar->name;?>">            <a href="/<?=CATALOG_URL;?>/tovarFull/<?=$tovar->id;?>" class="underline black">              <?=$tovar->name;?>            </a>          </div>          <a href="/<?=CATALOG_URL;?>/tovarFull/<?=$tovar->id;?>">            <div class="thumb">              <? if($tovar->photo_main):?>                <img src="<?=$tovar->photo_main;?>" alt="Фото" title="<?=$tovar->name;?>" />              <? else:?>                <img src="/images/base/nophoto.jpg" alt="Фото отсутствует" border="0" title="<?=$tovar->name;?>" />              <? endif;?>            </div>          </a>          <div class="price"><?=$tovar->price;?> р.</div>        </div>        <div class="bottom"></div>        <div class="button">          <input type="image" class="buy_button" src="/images/main/buy_button.png" onClick="buyTovar(<?=$tovar->id;?>);" id="to_cart_button<?=$tovar->id;?>" />          <span id="to_cart_button_action_notify<?=$tovar->id;?>"></span>        </div>      </li>      <? endforeach;?>    </ul>    <? else:?>      <div align="center">По выбранным условиям товаров не найдено.</div>    <? endif;?>  <? if ($page_config['paginator']):?>    <hr class="clear" />    <? if ($tovars['pages'] > 1):?>    <ul class="pagination">      <? if ($tovars['page']==1):?>        <li class="disabled"><a href="#">&laquo;</a></li>      <? else:?>        <li><a class="prev" href="<?=$pre_link;?>nav/page/<?=($tovars['page'] - 1);?>">&laquo;</a></li>      <? endif;?>      <? for ($i = 1; $i <= $tovars['pages']; $i++):?>        <? if (            $tovars['pages'] > 10 &&             ($tovars['pages'] - $i >= 3) &&             ($i > 3) &&             ( ($i > $tovars['page'] + 5 ) || ($i < $tovars['page'] - 5 ) )          ):?>          <? continue;?>        <? endif;?>        <? if ($i - $last_printed > 1):?><li class="disabled"><a href="#">...</a></li><? endif;?>        <? if ($i==$tovars['page']):?>          <li class="active"><a href="#"><?=$tovars['page'];?></a></li>        <? else:?>          <li><a href="<?=$pre_link;?>nav/page/<?=$i;?>"><?=$i;?></a></li>        <? endif;?>        <? $last_printed = $i;?>      <? endfor;?>      <? if ($tovars['page']>=$tovars['pages']):?>        <li class="disabled"><a href="#">&raquo;</a></li>      <? else:?>        <li><a href="<?=$pre_link;?>nav/page/<?=($tovars['page'] + 1);?>">&raquo;</a></li>      <? endif;?>    </ul>    <? endif;?>  <? endif;?></div><script>function buyTovar(id) {  var button_obj = $("#to_cart_button"+id);  button_obj.hide();  $.post("/<?=CATALOG_URL;?>/zakaz/addToZakaz", {id:id, count:1}, function () {    button_obj.show();    $("#to_cart_button_action_notify"+id)      .text("Товар добавлен в корзину")      .addClass("ui-tooltip ui-corner-all ui-state-default")      .css('display','inline').css('margin', '-7px 0 0 5px')      .show({        effect: "fade"      })      .delay(1000)      .hide({        effect: "fade",        duration: "slow"      });    loadCart();  });}$(function() {  $("#catalog_container").find(".param").mouseenter(function() {    var obj = $(this).find(".param_detail");    obj.load("/<?=CATALOG_URL;?>/tovarParams", {tovar_id:obj.attr('tovId'), param:$(this).attr('class')});  })});</script>